function weightToHeight(weight) {
    var w = (weight-minWeight)/(maxWeight-minWeight)*maxHeight;
    return Math.round(w*10)/10;
}

function weightToY(weight) {
    var height = weightToHeight(weight);
    return liquidY + maxHeight - height;
}

