function isEnabled(status, bit) {
    return !!(status & (1<<bit))
}

async function updateFromLog(device, options) {
    const log = await device.getParsedCompactLog();
    const temp = log.parameters.A/100;
    const targetTemp = log.parameters.AA/100;
    const epoch = log.epoch * 1000;
    const paramStatus = log.parameters.Z;

    if(options.updateGraph) {
        var temperatures = API.getData('temperatures');
        temperatures.push(epoch);
        temperatures.push(temp);
        temperatures.triggerChange();    
    }


