script:
if(this.event === 'load') {
    console.log('Load event')
    var socket = io.connect('/');
    var arduinom = window.arduinom(socket);
    API.cache('arduinom', arduinom);

    var socket = io.connect('/');
    var arduinom = window.arduinom(socket);
    var devices = API.cache('devices');
    arduinom.on('newDevice', async function(device) {
        console.log('new device', device);
        if (! devices[device.id]) {
            console.log('adding event on ',device)
            devices[device.id]=device;
            var info = await device.getDeviceInformation();
            var epoch = await device.setEpochNow();
            UI.showNotification('Set epoch to '+epoch, 'info');
            device.parametersInformation={};
            info.parameters.forEach( (parameter) => {
                device.parametersInformation[parameter.label]=parameter;
            });
            updateDevicesArray();

            device.eventsInformation={};
            info.events.forEach( (event) => {
                device.eventsInformation[event.id]=event;
            });

            device.on('connect', () => {
                console.log('Conndected: ',device);
                updateDevicesArray();
            });
            device.on('disconnect', () => {
                console.log('Disconnected: ',device);
                updateDevicesArray();
            });
        }
    });
    arduinom.ready();
}



function updateDevicesArray() {
    var devices = API.cache('devices');
    var data=[];
    for (var deviceID of Object.keys(devices)) {
        var device=devices[deviceID];
        data.push({
            id:device.id,
            status:device.status,
            type:device.type,
            info: device.info,
            logs: device.logs
        })
    }
    API.createData('devices', data);
}


if(this.event === 'load') {
    updateDevicesArray();
}

if (this.variable==='selectedParameters') {
    updateChart();
    updateAnnotations();
}


function updateAnnotations() {
    var devices = API.cache('devices');
    var deviceID = API.getData('deviceID');
    var device = devices[String(deviceID)];
    var logs=device.logs.filter( a => a.event>0 );
    var annotations=[];
    var colors=['red','blue','yellow','green','black'];
    for (var i=0; i<logs.length; i++) {
        var log=logs[i];
        annotations.push({
            "fillColor": colors[i%colors.length],
            "type": "rect",
            "strokeColor": "red",
            "strokeWidth": "0px",
            "position": [
              {
                "x": log.epoch,
                "dx": "-1px",
                "y": "0px"

              },
              {
                "x": log.epoch,
                "dx": "1px",
                "y": "10px"
              }
            ],
            "label": 
              {
                "text": "",
                "position": {
                  "x": log.epoch,
                  "y": "20px"
                }
              },
              "info":log,
              "_highlight":log._highlight
          }
        )
    }
    API.createData('annotations', annotations);

}

function updateChart() {
    var colors=['red','blue','yellow','green','black'];
    var device = API.cache('devices')[API.getData('deviceID')];

    var group=[];
    var selectedParameters=API.getData('selectedParameters');

    if (selectedParameters && selectedParameters.length>0) {
        group=selectedParameters.map( a => a.label );
    }
    var series=[];
    var epochs=device.logs.map( log => Number(log.epoch) );
    for (var i=0; i<group.length; i++) {
        var key = group[i];
        if (typeof device.logs[0].parameters[key]!=="undefined") {
            var serie={};
            var factor=device.parametersInformation[key].factor || 1;
            if (device.parametersInformation[key] && device.parametersInformation[key].name) {
                serie.label=device.parametersInformation[key].name;
            } else {
                serie.label=key;
            }
            serie.options={
                lineColor:colors[i%colors.length],
                lineStyle:1
            };
            serie.x=epochs;
            serie.y=device.logs.map( log => log.parameters[key]/factor);
            series.push(serie);
        }
    }
    API.createData('chart',{
        data: series
    });
}

var previousDeviceID=API.cache('previousDeviceID') || -1;
var deviceID=Number(API.getData('deviceID'));
if ((this.button || this.variable==='deviceID') && deviceID!=previousDeviceID) {
    API.cache('previousDeviceID',deviceID);
    if (API.cache('getDevicesInfo')) clearInterval(API.cache('getDevicesInfo'));
    console.time('start');
    async function getDevicesInfo() {
        if (! API.getData('preferences').autoUpdateSettings.resurrect()) return;
        var device = API.cache('devices')[API.getData('deviceID')];
        console.log('update info for ',device.id, device.status);
        if (device.status==='connect') {
            device.info = await device.getCurrentDeviceInformation()
        }
        updateDevicesArray();
    }
    await getDevicesInfo();
    console.timeEnd('start');
    API.cache('getDevicesInfo',window.setInterval( getDevicesInfo, 10000));

     /**
     * We maintain the last XXX entries in browser
     */
    if (API.cache('updateDevicesLogs')) clearInterval(API.cache('updateDevicesLogs'));
    async function updateDevicesLogs() {
        if (! API.getData('preferences').autoUpdateLogs.resurrect()) return;
        var limit=10000;
        var device = API.cache('devices')[API.getData('deviceID')];

        if (!device.logs) device.logs=[];
        var lastEpoch=0;

        if (device.logs.length>0) lastEpoch=device.logs[device.logs.length-1].epoch;
        var httpRequest = await fetch('/db/'+device.id+'/entries?limit='+limit+'&since='+lastEpoch);
        var result= await httpRequest.json();
        result.forEach( a => {
            a.epoch*=1000;
            a._highlight=Math.random();
        });
        device.logs=device.logs.concat(result);
        device.logs.sort( (a,b) => a.epoch - b.epoch );
        if (device.logs.length>limit) {
            device.logs=device.logs.slice(device.logs.length-limit);
        }

        device.logs.forEach( a => {
            if (a.event>0) {
                console.log(a.event,device.eventsInformation[a.event]);
                a.eventLabel=device.eventsInformation[a.event];
            }
        });
        updateDevicesArray();
        updateChart();
    }
    API.cache('updateDevicesLogs',window.setInterval( updateDevicesLogs, 10000));



    if (API.cache('getFreeMemory')) clearInterval(API.cache('getFreeMemory'));
    async function getFreeMemory() {
        var device = API.cache('devices')[API.getData('deviceID')];
        if (device && device.status==='connect') {
            var memory = await device.getFormattedFreeMemory();
            API.createData('memory', memory);
        }
    }
    await getFreeMemory();
    API.cache('getFreeMemory',window.setInterval( getFreeMemory, 60000));




}

var parameter=API.getData('parameter');
var newValue=API.getData('setParameter').realValue*(parameter.factor || 1);
var device=API.cache('devices')[API.getData('deviceID')];
var minValue=parameter.min || -32768;
var maxValue=parameter.max || 32767;

console.log(device);

if (newValue < minValue || newValue > maxValue) {
    UI.showNotification(`Value in internal units should be between ${minValue} and ${maxValue}`);
} else {
    var result = await device.setParameter(parameter.label, newValue);
    if (Number(result) === newValue) {
        UI.showNotification('Parameter changed successfully to '+result,'info');
    } else {
        UI.showNotification('Something went wrong, the new value is '+result,'error');
    }

}

var command=API.getData('command').value+'';
console.log(command);
var device = API.cache('devices')[API.getData('deviceID')];
if (device && device.status==='connect') {
    var result = await device.addRequest(command+'');
    console.log(result);
    API.createData('commandResult', result);
}

var device = API.cache('devices')[API.getData('deviceID')];
if (device && device.status==='connect') {
    var memory = await device.getFormattedFreeMemory();
        console.log(memory);
    API.createData('memory', memory);
}

window.devices={};
API.createData('devices', []);
API.cache('devices', window.devices);

API.createData('preferences', {
    autoUpdateSettings: true,
    autoUpdateLogs: true,
    fromID: '',
    numberLogs: 10000
});
