script:
setAsync();

API.loading('processing','Acquisition in prograss');
var resultCallback=function(result) {
    if ((result.status==="info") || (result.status==="warn")){
        API.loading("processing",result.message+": "+result.data, result.status);
    } else {
        set('result',result);
        API.stopLoading('processing');
        if (result.status==="error") {
            UI.showNotification(result.message, 
                { className:'error', autoHide: false }
            );
        }
        done();
    }
}

postChromeMessage('serial.send','r',resultCallback);

var method="EXP";

var experiments=API.getData('experiments');

var data=API.getData('data');

var info=getAction().value;


if (! info.name) {
    UI.dialog("Please enter a name for this experiment");
} else if (checkExists(info)) {
    UI.dialog("This experiment already exists, please delete it first or change the name / comment");
} else {
    experiments.push(
        {   name: info.name,
            comment: info.comment,
            concentration: info.concentration,
            method: method,
            date: new Date().getTime(),
            data: data
        }
    );
    experiments.triggerChange();
}


function checkExists(info) {
    for (var i=0; i<experiments.length; i++) {
        var experiment=experiments[i];
        if ((experiment.concentration==info.concentration) && (experiment.name==info.name) && (experiment.comment==info.comment)) {
            return true;
        }
    }
    return false;
}

var spectra=get('data');
var options=get('options');



//var spectra=OSP.parse(data, options);



var annotations=OSP.getAnnotations(spectra.A);
var chartTransmittance=OSP.getChart(spectra,{channels:'T'});
var chartAbsorbance=OSP.getChart(spectra,{channels:'A'});

var spectraArray=OSP.Util.toArray(spectra);


// we will create a table with all the information


var table=[];
if (spectraArray && spectraArray.length>0) {
    var x=spectraArray[0].x;
    for (var i=0; i<x.length; i++) {
        console.log("X")
        var line={x:x[i]};
        table.push(line);
        for (var j=0; j<spectraArray.length; j++) {
            line[spectraArray[j].type]=spectraArray[j].y[i];
        }
    }
}

set('chartTransmittance', chartTransmittance);
set('chartAbsorbance', chartAbsorbance);
set('annotations', annotations);
set('spectra', spectra);
set('spectraArray', spectraArray);
set('table', table);
console.log(chartTransmittance);
console.log(chartAbsorbance);

var openSpectro=require.node('./openspectro');
var util=require.node('./util');
const Serial = require.node("./SerialDevices");
const SerialQManager = require.node("./SerialQueueManager");
const pouchDB = require.node("./pouch");


var spectro = API.cache('spectro'); //create a device spectro for address 'S0' = 21296

if (!spectro) {
spectro = new openSpectro(util.deviceIdStringToNumber('S0'));    
    API.cache('spectro', spectro);    
}

if(spectro._ready) {
    //spectro.runExperiment().then(
  spectro.getExperimentResults().then(
        (res)=> API.createData("results",res.docs)).catch((err)=> API.createData("error",err.message));

}

var experiments=[];
var data=localStorage.getItem('spectro.experiments');
if (data) {
    try {
        var parsed=JSON.parse(data);
        if (Array.isArray(parsed)) experiments=parsed;
    } catch (exception) {
        console.log(exception);
    }
}

API.createData('experiments', experiments);

var data = require('src/util/versioning').getData();
data.onChange(function (evt) {
    if(evt.jpath.length==1 && evt.jpath[0]=='experiments') {
        localStorage.setItem('spectro.experiments', JSON.stringify(experiments));
    }
});
